const CategoryTabs = ({}) => {
  const [activeTab, setActiveTab] = useState(-1);
  useEffect(() => {
    let path = window.location.pathname;
    if (path === "/") {
      //setActiveTab(0); // auto by default
    } else {
      setActiveTab(CategoryItems.findIndex((item) => item.path.endsWith(path)));
    }
  }, []);

  const isDesktopOrLaptop = useMediaQuery({
    query: "(max-width: 1024px)",
  });
  return (
    <>
      <div className="bannerIntialContainer">
        <span>Let's find the</span>
        <p style={{ margin: 0 }}>
          <b>Best</b>
          <b style={{ color: "red", marginLeft: 10 }}>Takaful Plan</b>
        </p>
        <p style={{ margin: 0, fontSize: 18, marginBottom: 30 }}>
          Pakistan's only Digital Takaful Solution for All
        </p>
      </div>
      <div className="px-2 w-100 d-flex align-items-center justify-content-around category-group">
        {CategoryItems.map((item, index) =>
          isDesktopOrLaptop ? (
            <Link href={item.path}>
              <div
                className={`d-flex flex-column align-items-center justify-content-center ${
                  styles["category-btn"]
                } ${index === activeTab ? styles["active"] : ""}`}
              >
                <div
                  style={{
                    textAlign: "center",
                    padding: "5px",
                  }}
                >
                  {item.box}
                  <p className="categoryLabel">{item.label}</p>
                </div>
              </div>
            </Link>
          ) : (
            <Link href={item.path}>
              <div
                className={`d-flex flex-column align-items-center justify-content-center ${
                  styles["category-btn"]
                } ${index === activeTab ? styles["active"] : ""}`}
              >
                <div
                  className={` d-flex align-items-center justify-content-center  ${styles["icons"]}`}
                >
                  <div style={{ textAlign: "center" }}>
                    {item?.icon}
                    <p className="categoryLabel">{item.label}</p>
                  </div>
                </div>
                {/* <p className={styles["category-btn-label"]}>{item.label}</p> */}
              </div>
            </Link>
          )
        )}
      </div>
    </>
  );
};

export default CategoryTabs;
